import React, { forwardRef } from 'react'
import Select from 'react-select'
import DatePicker from 'react-datepicker'
import 'react-datepicker/dist/react-datepicker.css'
import moment from 'moment'

import './styles.scss'
import { ArrowBackIcon, CalendarIcon, NextArrowIcon } from '../assets/svg';

const projects = [
  { value: 'colafizz', label: 'Colafizz' },
  { value: 'rest', label: 'Rest' },
  { value: 'twiine', label: 'Twine' },
];

const categories = [
  { value: 'development', label: 'Development' },
  { value: 'investigation', label: 'Investigation' },
  { value: 'meeting', label: 'StandUp Meeting' },
  { value: 'ps4', label: 'PS4' },
];

export const Developer = ({
  logOut,
  user,
  selectedProject,
  setSelectedProject,
  selectedCategory,
  setSelectedCategory,
  value,
  handleChange,
  create,
  jobsData,
  remove,
  startDate,
  setStartDate,
  usersArray,
  selectedUser,
  setSelectedUser,
  users,
  clearFilter
}) => {

  const ExampleCustomInput = forwardRef(
    ({ value, onClick }, ref) => (
      <div onClick={onClick} ref={ref} className="example-custom-input">
        <CalendarIcon/>
      </div>
    ),
  );

  return (
    <div>
      <div className='header'>
        <div>{user.name} { user.second_name }</div>
        <button onClick={ () => logOut() }>Log out</button>
      </div>
      {
        !user.isAdmin ? (
          <div className='select-block'>
            <Select
              value={ selectedProject }
              onChange={ (opt) => setSelectedProject(opt) }
              options={ projects }
              className='select'
              placeholder='Project'
            />
            <Select
              value={ selectedCategory }
              onChange={ (opt) => setSelectedCategory(opt) }
              options={ categories }
              className='select'
              placeholder='Category'
            />
            <input
              placeholder='Hours'
              type="text"
              pattern="[0-9]*"
              onInput={ (e) => handleChange(e) }
              value={ value }
            />
            <button onClick={ () => create() }>
              Save
            </button>
          </div>
        ) : (
          <div style={{ display: 'flex' }}>
            <div>Filters:</div>
            <div className='select-block'>
              <div style={{ display: 'flex', width: '40%' }}>
                <Select
                  value={ selectedUser }
                  onChange={ (opt) => setSelectedUser(opt) }
                  options={ usersArray }
                  className='select'
                  placeholder='User'
                />
                <button onClick={ () => setSelectedUser(null) }>Clear</button>
              </div>
              <div style={{ display: 'flex', width: '40%' }}>
                <Select
                  value={ selectedProject }
                  onChange={ (opt) => setSelectedProject(opt) }
                  options={ projects }
                  className='select'
                  placeholder='Project'
                />
                <button onClick={ () => setSelectedProject(null) }>Clear</button>
              </div>
              <div style={{ display: 'flex', width: '40%' }}>
                <Select
                  value={ selectedCategory }
                  onChange={ (opt) => setSelectedCategory(opt) }
                  options={ categories }
                  className='select'
                  placeholder='Category'
                />
                <button onClick={ () => setSelectedCategory(null) }>Clear</button>
              </div>
            </div>
          </div>
        )
      }
      <div className='date-picker'>
        <button onClick={ () => setStartDate(new moment(startDate).subtract(1, 'days').toDate()) }>
          <ArrowBackIcon />
        </button>
        { moment(startDate).format('LL') }
        <DatePicker
          selected={ startDate }
          onChange={ date => setStartDate(date) }
          customInput={ <ExampleCustomInput /> }
        />
        <button onClick={ () => setStartDate(new moment(startDate).add(1, 'days').toDate()) }>
          <NextArrowIcon/>
        </button>
      </div>
      <div className='data-block'>
        <div className='data-header'>
          { user.isAdmin && <div className='data-item'>User</div> }
          <div className='data-item'>Project</div>
          <div className='data-item'>Category</div>
          <div className='data-item'>Hours</div>
          <div className='data-item'>Actions</div>
        </div>
        {
          jobsData.length > 0 && jobsData.map( item => (
            <div className='data-body' key={ item.id }>
              { user.isAdmin && users.map(i => {
                if (i.id === item.user_id) {
                  return <div className='data-item'>{ i.name  }</div>
                } 
              })
              }
              <div className='data-item'>{ item.name }</div>
              <div className='data-item'>{ item.category }</div>
              <div className='data-item'>{ item.hours }</div>
              <div className='data-item'>
                <button>Edit</button>
                <button onClick={ () => remove(item.id) } >Delete</button>
              </div>
            </div>
          ))
        }
        <div className='total'>
          Total: { jobsData.reduce(function(prev, cur) { return prev + cur.hours }, 0) }
        </div>
      </div>
    </div>
  )
}
