import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import isEmpty from 'lodash.isempty'

import { LogIn } from '../components'
import { sessionCreateRequest } from '../store/sessions/actions'
// import { history } from '../../services'

const initialState = {
  email: '',
  password: ''
}

export const LogInContainer = () => {
  const dispatch = useDispatch()

  const [value, setValue] = useState(initialState)

  const { data } = useSelector(state => state.sessions)

  const handleChange = (key, text) => {
    setValue({...value, [key]: text})
    // errorsState[key] && setErrorsState({...errorsState, [key]: ''})
  }

  const submit = () => {
    dispatch(sessionCreateRequest('authentications', value))
  }

  return (
    <LogIn
      handleChange={ handleChange }
      submit={ submit }
    />
  )
}
