import React, { forwardRef } from 'react'
import Select from 'react-select'
import DatePicker from 'react-datepicker'
import 'react-datepicker/dist/react-datepicker.css'
import moment from 'moment'
import 'react-responsive-modal/styles.css'
import { Modal } from 'react-responsive-modal'

import './styles.scss'
import { ArrowBackIcon, CalendarIcon, CencelIcon, NextArrowIcon } from '../assets/svg';
import { isEmpty } from 'lodash'

const projects = [
  { value: 'colafizz', label: 'Colafizz' },
  { value: 'rest', label: 'Rest' },
  { value: 'twiine', label: 'Twine' },
];

const categories = [
  { value: 'development', label: 'Development' },
  { value: 'investigation', label: 'Investigation' },
  { value: 'meeting', label: 'StandUp Meeting' },
  { value: 'ps4', label: 'PS4' },
];

export const Developer = ({
  logOut,
  user,
  selectedProject,
  setSelectedProject,
  selectedCategory,
  setSelectedCategory,
  value,
  handleChange,
  create,
  jobsData,
  remove,
  startDate,
  setStartDate,
  usersArray,
  selectedUser,
  setSelectedUser,
  users,
  getJob,
  open,
  setModalOpen,
  job,
  flag,
  updateJob,
  selectedEditProject,
  setSelectedEditProject,
  selectedEditCategory,
  setSelectedEditCategory,
  valueEdit
}) => {

  const ExampleCustomInput = forwardRef(
    ({ value, onClick }, ref) => (
      <div onClick={onClick} ref={ref} className="example-custom-input">
        <CalendarIcon/>
      </div>
    ),
  );

  return (
    <div>
      <div className='header'>
        <div>{user.name} { user.second_name }</div>
        <button onClick={ () => logOut() }>Log out</button>
      </div>
      {
        !user.isAdmin ? (
          <div className='select-block'>
            <Select
              value={ selectedProject }
              onChange={ (opt) => setSelectedProject(opt) }
              options={ projects }
              className='users-select'
              placeholder='Project'
            />
            <Select
              value={ selectedCategory }
              onChange={ (opt) => setSelectedCategory(opt) }
              options={ categories }
              className='users-select'
              placeholder='Category'
            />
            <input
              placeholder='Hours'
              type="text"
              pattern="[0-9]*"
              onInput={ (e) => handleChange(e) }
              value={ value }
            />
            <button onClick={ () => create() }>
              Save
            </button>
          </div>
        ) : (
          <div style={{ display: 'flex', width: '80%', margin: '50px auto 0', alignItems: 'center' }}>
            <div style={{ marginRight: '40px', fontSize: '25px' }}>Filters:</div>
            <div className='select-block'>
              <div style={{ display: 'flex', width: '40%', alignItems: 'center' }}>
                <Select
                  value={ selectedUser }
                  onChange={ (opt) => setSelectedUser(opt) }
                  options={ usersArray }
                  className='select'
                  placeholder='User'
                />
                <div onClick={ () => setSelectedUser(null) } style={{ marginLeft: '10px' }}>
                  <CencelIcon/>
                </div>
              </div>
              <div style={{ display: 'flex', width: '40%', alignItems: 'center' }}>
                <Select
                  value={ selectedProject }
                  onChange={ (opt) => setSelectedProject(opt) }
                  options={ projects }
                  className='select'
                  placeholder='Project'
                />
                <div onClick={ () => setSelectedProject(null) } style={{ marginLeft: '10px' }}>
                  <CencelIcon/>
                </div>
              </div>
              <div style={{ display: 'flex', width: '40%', alignItems: 'center' }}>
                <Select
                  value={ selectedCategory }
                  onChange={ (opt) => setSelectedCategory(opt) }
                  options={ categories }
                  className='select'
                  placeholder='Category'
                />
                <div onClick={ () => setSelectedCategory(null) } style={{ marginLeft: '10px' }}>
                  <CencelIcon/>
                </div>
              </div>
            </div>
          </div>
        )
      }
      <div className='date-picker'>
        <button onClick={ () => setStartDate(new moment(startDate).subtract(1, 'days').toDate()) } style={{ display: 'flex', alignItems: 'center' }}>
          <ArrowBackIcon />
        </button>
        { moment(startDate).format('LL') }
        <DatePicker
          selected={ startDate }
          onChange={ date => setStartDate(date) }
          customInput={ <ExampleCustomInput /> }
        />
        <button onClick={ () => setStartDate(new moment(startDate).add(1, 'days').toDate()) }>
          <NextArrowIcon/>
        </button>
      </div>
      <div className='data-block'>
        <div className='data-header'>
          { user.isAdmin && <div className='data-item'>User</div> }
          <div className='data-item'>Project</div>
          <div className='data-item'>Category</div>
          <div className='data-item'>Hours</div>
          <div className='data-item'>Actions</div>
        </div>
        {
          jobsData.length > 0 && jobsData.map( item => (
            <div className='data-body' key={ item.id }>
              { user.isAdmin && users.map(i => {
                if (i.id === item.user_id) {
                  return <div className='data-item'>{ i.name  }</div>
                } 
              })
              }
              <div className='data-item'>{ item.name }</div>
              <div className='data-item'>{ item.category }</div>
              <div className='data-item'>{ item.hours }</div>
              <div className='data-item'>
                <button onClick={ () => getJob(item.id) } >Edit</button>
                <button onClick={ () => remove(item.id) } >Delete</button>
              </div>
            </div>
          ))
        }
        <div className='total'>
          Total: { jobsData.reduce(function(prev, cur) { return prev + cur.hours }, 0) }
        </div>
      </div>
      {
        !isEmpty(job) && (
          <Modal
            open={open}
            onClose={() => setModalOpen(false)}
            center
            classNames={{
              modal: 'customModal',
            }}
          >
            <h2>Edit</h2>
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <Select
                value={
                  selectedEditProject || {
                    value: job.name,
                    label: job.name.charAt(0).toUpperCase() + job.name.slice(1)
                }}
                onChange={ (opt) => setSelectedEditProject(opt) }
                options={ projects }
                className='select'
                placeholder='Project'
              />
              <Select
                value={ selectedEditCategory || {
                  value: job.category,
                  label: job.category.charAt(0).toUpperCase() + job.category.slice(1)
                }}
                onChange={ (opt) => setSelectedEditCategory(opt) }
                options={ categories }
                className='select'
                placeholder='Category'
              />
              <input
                placeholder='Hours'
                type="text"
                pattern="[0-9]*"
                onInput={ (e) => handleChange(e) }
                value={ flag ? valueEdit : job.hours }
              />
            </div>
            <div style={{
              position: 'absolute',
              bottom: '15px',
              width: '95%',
              margin: '0 auto',
              textAlign: 'center'
            }}>
              <button
                style={{ fontSize: '20px' }}
                onClick={ () => updateJob() }
              >
                Save
              </button>
            </div>
          </Modal>
        )
      }
    </div>
  )
}
