import * as constant from './actions'

const initialResourceState = {
  jobsData: [],
  loading: false,
  errors: {}
}

export default (state = initialResourceState, { type, payload }) => {
  switch (type) {

  case constant.GET_JOBS_REQUEST:
    return {
      ...state,
      loading: true
    }
  case constant.GET_JOBS_SUCCESS:
    return {
      ...state,
      loading: false,
      jobsData: payload.job
    }
  case constant.GET_JOBS_FAILURE:
    return {
      ...state,
      loading: false,
      errors: payload
    }

  case constant.SESSION_CREATE_REQUEST:
    return {
      ...state,
      loading: true
    }
  case constant.SESSION_CREATE_SUCCESS:
    return {
      ...state,
      loading: false,
      data: payload
    }
  case constant.SESSION_CREATE_FAILURE:
    return {
      ...state,
      loading: false,
      errors: payload
    }

  // case constant.SESSION_DELETE_REQUEST:
  //   return {
  //     ...state,
  //     loading: true
  //   }
  // case constant.SESSION_DELETE_SUCCESS:
  //   return {
  //     ...state,
  //     loading: false,
  //     data: {}
  //   }
  // case constant.SESSION_DELETE_FAILURE:
  //   return {
  //     ...state,
  //     loading: false,
  //     errors: payload
  //   }

  default:
    return state
  }
}
