import React, { useEffect, useState } from 'react'
import Select from 'react-select'

import './styles.scss'

const projects = [
  { value: 'Colafizz', label: 'Colafizz' },
  { value: 'Rest', label: 'Rest' },
  { value: 'Twine', label: 'Twine' },
];

const categories = [
  { value: 'Development', label: 'Development' },
  { value: 'Investigation', label: 'Investigation' },
  { value: 'StandUp Meeting', label: 'Meeting' },
  { value: 'PS4', label: 'PS4' },
];

export const Developer = ({
  logOut,
  user,
  selectedOption,
  setSelectedOption,
  value,
  handleChange
}) => {

  return (
    <div>
      <div className='header'>
        <div>{user.name} { user.second_name }</div>
        <button onClick={ () => logOut() }>Log out</button>
      </div>
      <div className='select-block'>
        <Select
          value={ selectedOption }
          onChange={ (opt) => setSelectedOption(opt) }
          options={ projects }
          className='select'
          placeholder='Project'
        />
        <Select
          value={ selectedOption }
          onChange={ (opt) => setSelectedOption(opt) }
          options={ categories }
          className='select'
          placeholder='Category'
        />
        <input
          placeholder='Hours'
          type="text"
          pattern="[0-9]*"
          onInput={ (e) => handleChange(e) }
          value={ value }
        />
        <button>Save</button>
      </div>
    </div>
  )
}
